import React, { useState, useRef } from 'react';
import styled, { keyframes } from 'styled-components';

// Keyframe animations
const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const slideInLeft = keyframes`
  from {
    opacity: 0;
    transform: translateX(-30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

const float = keyframes`
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-5px);
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -200px 0;
  }
  100% {
    background-position: calc(200px + 100%) 0;
  }
`;

const sectionPulse = keyframes`
  0% {
    transform: scale(1);
    box-shadow: 0 0 0 0 rgba(201, 184, 168, 0.4);
  }
  50% {
    transform: scale(1.02);
    box-shadow: 0 0 0 20px rgba(201, 184, 168, 0);
  }
  100% {
    transform: scale(1);
    box-shadow: 0 0 0 0 rgba(201, 184, 168, 0);
  }
`;

const tabSlideIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(-10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const glowPulse = keyframes`
  0%, 100% {
    box-shadow: 0 4px 15px rgba(10, 59, 30, 0.1);
  }
  50% {
    box-shadow: 0 6px 25px rgba(10, 59, 30, 0.3);
  }
`;

// Styled Components
const TeamPageContainer = styled.div`
  min-height: 100vh;
  background-color: #f8e8d8;
  padding: 40px 0;
  font-family: 'Inter', 'Arial', sans-serif;
`;

const TeamHeader = styled.div`
  text-align: center;
  background-color: #f8e8d8;
  padding: 60px 20px 80px;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 100px;
    height: 3px;
    background-color: #c9b8a8;
    border-radius: 2px;
  }
`;

const MeetOurTeam = styled.h1`
  font-size: 3.5rem;
  font-weight: 800;
  color: #0a3b1e;
  margin: 0 0 20px 0;
  letter-spacing: -1px;
  line-height: 1.1;
  font-family: 'Playfair Display', serif;

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }

  @media (max-width: 480px) {
    font-size: 2rem;
  }
`;

const MeetText = styled.span`
  display: block;
  font-size: 1.2rem;
  font-weight: 500;
  color: #c9b8a8;
  margin-bottom: 10px;
  letter-spacing: 2px;
  text-transform: uppercase;
  font-family: 'Inter', sans-serif;
`;

const OurText = styled.span`
  color: #0a3b1e;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 100%;
    height: 3px;
    background-color: #c9b8a8;
    border-radius: 2px;
  }
`;

const TeamText = styled.span`
  color: #0a3b1e;
  font-weight: 700;
`;

const TeamDescription = styled.p`
  font-size: 1.2rem;
  color: #0a3b1e;
  max-width: 600px;
  margin: 30px auto 0;
  line-height: 1.6;
  opacity: 0.8;
  font-weight: 400;
  font-family: 'Crimson Text', serif;

  @media (max-width: 768px) {
    font-size: 1.1rem;
    padding: 0 20px;
  }
`;

const DepartmentsSection = styled.div`
  text-align: center;
  margin: 60px 0;
  padding: 0 20px;
`;

const DepartmentButton = styled.button`
  background-color: ${props => props['data-active'] === 'true' ? '#0a3b1e' : '#c9b8a8'};
  color: ${props => props['data-active'] === 'true' ? '#f8e8d8' : '#0a3b1e'};
  border: none;
  padding: 15px 30px;
  margin: 0 10px 15px;
  border-radius: 30px;
  cursor: pointer;
  font-size: 1rem;
  font-weight: 600;
  letter-spacing: 0.5px;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: ${props => props['data-active'] === 'true' ? '0 6px 20px rgba(10, 59, 30, 0.3)' : '0 4px 15px rgba(10, 59, 30, 0.1)'};
  position: relative;
  overflow: hidden;
  animation: ${tabSlideIn} 0.6s ease-out ${props => props['data-delay'] || 0}s both;
  font-family: 'Poppins', sans-serif;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(248, 232, 216, 0.3), transparent);
    transition: left 0.6s;
  }

  &:hover {
    background-color: #0a3b1e;
    color: #f8e8d8;
    transform: translateY(-3px) scale(1.05);
    box-shadow: 0 8px 25px rgba(10, 59, 30, 0.4);
    animation: ${glowPulse} 2s infinite;

    &::before {
      left: 100%;
    }
  }

  &:active {
    transform: translateY(-1px) scale(1.02);
  }

  ${props => props['data-active'] === 'true' && `
    animation: ${glowPulse} 2s infinite;
    transform: translateY(-2px);
  `}

  @media (max-width: 480px) {
    padding: 12px 24px;
    font-size: 0.9rem;
    margin: 0 5px 10px;
  }
`;

const TeamSections = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const TeamSection = styled.div`
  margin-bottom: 80px;
  transition: all 0.3s ease;

  &:last-child {
    margin-bottom: 40px;
  }

  &.pulse-animation {
    animation: ${sectionPulse} 1s ease-out;
  }
`;

const SectionTitle = styled.h2`
  font-size: 2.5rem;
  font-weight: 700;
  color: #0a3b1e;
  text-align: center;
  margin-bottom: 50px;
  position: relative;
  font-family: 'Playfair Display', serif;

  &::before {
    content: '';
    position: absolute;
    top: -15px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 4px;
    background-color: #c9b8a8;
    border-radius: 2px;
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -15px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 2px;
    background-color: #c9b8a8;
    border-radius: 1px;
    opacity: 0.6;
  }

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const MembersGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 30px;
  justify-items: center;

  @media (max-width: 480px) {
    grid-template-columns: 1fr;
    gap: 25px;
  }
`;

const MemberCard = styled.div`
  background: #f8e8d8;
  border-radius: 20px;
  padding: 30px 20px;
  text-align: center;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  border: 2px solid transparent;
  box-shadow: 0 8px 25px rgba(10, 59, 30, 0.08);
  position: relative;
  overflow: hidden;
  animation: ${fadeInUp} 0.8s ease-out ${props => props['data-delay'] || 0}s both;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #c9b8a8, #0a3b1e, #c9b8a8);
    border-radius: 20px 20px 0 0;
    transition: all 0.3s ease;
  }

  &.leadership-role::before {
    background: linear-gradient(90deg, #0a3b1e, #c9b8a8, #0a3b1e);
    height: 6px;
  }

  &:hover {
    transform: translateY(-12px) scale(1.02);
    border-color: #c9b8a8;
    box-shadow: 0 20px 50px rgba(10, 59, 30, 0.2);

    &::before {
      height: 6px;
      background: linear-gradient(90deg, #0a3b1e, #c9b8a8, #0a3b1e);
    }
  }
`;

const MemberName = styled.h3`
  font-size: 1.3rem;
  font-weight: 600;
  color: #0a3b1e;
  margin: 0;
  letter-spacing: 0.5px;
  font-family: 'Poppins', sans-serif;

  &.leadership-role {
    font-size: 1.4rem;
    font-weight: 700;
  }
`;

const MemberRole = styled.p`
  font-size: 1rem;
  color: #c9b8a8;
  margin: 0;
  font-weight: 500;
  font-style: italic;
  letter-spacing: 0.5px;
  font-family: 'Crimson Text', serif;

  &.president, &.vice-president, &.director {
    color: #0a3b1e;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 1px;
    font-family: 'Inter', sans-serif;
    font-style: normal;
  }

  &.head {
    color: #c9b8a8;
    font-weight: 600;
  }

  &.jr-head {
    color: rgba(201, 184, 168, 0.8);
    font-weight: 500;
  }
`;

const MemberImageStyled = styled.div`
  width: 120px;
  height: 120px;
  border-radius: 50%;
  margin: 0 auto 20px;
  border: 4px solid #c9b8a8;
  overflow: hidden;
  transition: all 0.3s ease;
  position: relative;

  &:hover {
    border-color: #0a3b1e;
    transform: scale(1.05);
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const PlaceholderImage = styled.div`
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, #c9b8a8 0%, #0a3b1e 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: #f8e8d8;
  font-size: 2.5rem;
  font-weight: 700;
  letter-spacing: 1px;
`;

const PlaceholderText = styled.div`
  color: #f8e8d8;
  font-size: 2.5rem;
  font-weight: 700;
`;

const MemberInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const MotivationalQuote = styled.div`
  font-size: 1.1rem;
  color: #0a3b1e;
  font-style: italic;
  max-width: 700px;
  margin: 30px auto 0;
  text-align: center;
  line-height: 1.6;
  position: relative;
  padding: 20px 40px;
  font-family: 'Crimson Text', serif;

  &::before {
    content: '"';
    position: absolute;
    left: 10px;
    top: 0;
    font-size: 3rem;
    color: #c9b8a8;
    font-family: 'Playfair Display', serif;
  }

  &::after {
    content: '"';
    position: absolute;
    right: 10px;
    bottom: -10px;
    font-size: 3rem;
    color: #c9b8a8;
    font-family: 'Playfair Display', serif;
  }
`;

const DepartmentTitle = styled.h2`
  font-size: 2.2rem;
  font-weight: 700;
  color: #0a3b1e;
  margin-bottom: 10px;
  text-align: center;
  font-family: 'Playfair Display', serif;
`;

const DepartmentSubtitle = styled.p`
  font-size: 1.1rem;
  color: #c9b8a8;
  font-style: italic;
  text-align: center;
  margin-bottom: 30px;
  font-weight: 500;
  font-family: 'Crimson Text', serif;
`;

const SectionMotivation = styled.div`
  font-size: 1rem;
  color: #c9b8a8;
  font-style: italic;
  text-align: center;
  margin-bottom: 30px;
  font-weight: 500;
  letter-spacing: 0.5px;
  font-family: 'Crimson Text', serif;
`;

const MemberInspiration = styled.div`
  font-size: 0.85rem;
  color: rgba(10, 59, 30, 0.7);
  font-style: italic;
  text-align: center;
  margin-top: 8px;
  line-height: 1.4;
  padding: 8px 0;
  border-top: 1px solid rgba(201, 184, 168, 0.3);
  font-family: 'Crimson Text', serif;
`;

const TeamFooterSection = styled.div`
  background: linear-gradient(135deg, #0a3b1e 0%, rgba(10, 59, 30, 0.9) 100%);
  color: #f8e8d8;
  padding: 60px 20px;
  text-align: center;
  margin-top: 60px;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #c9b8a8, #f8e8d8, #c9b8a8);
  }
`;

