import React from 'react';
import styled, { keyframes } from 'styled-components';

const Header = () => {
  return (
    <HeaderContainer>
      <LogoSection>
        <Logo>LITCLUB</Logo>
        <LogoSubtext>Literary Excellence</LogoSubtext>
      </LogoSection>
      <Nav>
        <NavItem>HOME</NavItem>
        <NavItem>ABOUT</NavItem>
        <NavItem>SERVICES</NavItem>
      </Nav>
    </HeaderContainer>
  );
};

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(-10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const HeaderContainer = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 2rem 3rem;
  background: linear-gradient(135deg, #0a3b1e 0%, #0d4a26 100%);
  color: #f8e8d8;
  box-shadow: 0 4px 20px rgba(10, 59, 30, 0.3);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><defs><pattern id="grain" width="100" height="100" patternUnits="userSpaceOnUse"><circle cx="25" cy="25" r="1" fill="%23f8e8d8" opacity="0.05"/><circle cx="75" cy="75" r="1" fill="%23f8e8d8" opacity="0.05"/><circle cx="50" cy="10" r="0.5" fill="%23f8e8d8" opacity="0.03"/><circle cx="10" cy="60" r="0.5" fill="%23f8e8d8" opacity="0.03"/><circle cx="90" cy="40" r="0.5" fill="%23f8e8d8" opacity="0.03"/></pattern></defs><rect width="100" height="100" fill="url(%23grain)"/></svg>');
    pointer-events: none;
  }
`;

const LogoSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  animation: ${fadeIn} 0.8s ease-out;
`;

const Logo = styled.div`
  font-size: 2rem;
  font-weight: 900;
  letter-spacing: 4px;
  background: linear-gradient(45deg, #f8e8d8, #c9b8a8);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -4px;
    left: 0;
    width: 100%;
    height: 2px;
    background: linear-gradient(90deg, #c9b8a8, transparent);
    border-radius: 1px;
  }
`;

const LogoSubtext = styled.div`
  font-size: 0.7rem;
  letter-spacing: 2px;
  color: #c9b8a8;
  margin-top: 2px;
  font-style: italic;
  opacity: 0.9;
`;

const Nav = styled.nav`
  display: flex;
  gap: 3rem;
  animation: ${fadeIn} 0.8s ease-out 0.2s both;
`;

const NavItem = styled.a`
  color: #f8e8d8;
  text-decoration: none;
  font-size: 0.95rem;
  font-weight: 500;
  letter-spacing: 1px;
  cursor: pointer;
  position: relative;
  padding: 0.5rem 0;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);

  &::before {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, #c9b8a8, #f8e8d8);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    transform: translateX(-50%);
    border-radius: 1px;
  }

  &:hover {
    color: #c9b8a8;
    transform: translateY(-2px);

    &::before {
      width: 100%;
    }
  }

  &:active {
    transform: translateY(0);
  }
`;

export default Header;
