import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';

// Keyframe animations
const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const slideInLeft = keyframes`
  from {
    opacity: 0;
    transform: translateX(-30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

const float = keyframes`
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-5px);
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -200px 0;
  }
  100% {
    background-position: calc(200px + 100%) 0;
  }
`;

// Styled Components
const TeamPageContainer = styled.div`
  min-height: 100vh;
  background-color: #f8e8d8;
  padding: 40px 0;
  font-family: 'Arial', sans-serif;
`;

const TeamHeader = styled.div`
  text-align: center;
  background-color: #f8e8d8;
  padding: 60px 20px 80px;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 100px;
    height: 3px;
    background-color: #c9b8a8;
    border-radius: 2px;
  }
`;

const MeetOurTeam = styled.h1`
  font-size: 3.5rem;
  font-weight: 900;
  color: #0a3b1e;
  margin: 0 0 20px 0;
  letter-spacing: -1px;
  line-height: 1.1;

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }

  @media (max-width: 480px) {
    font-size: 2rem;
  }
`;

const MeetText = styled.span`
  display: block;
  font-size: 1.2rem;
  font-weight: 400;
  color: #c9b8a8;
  margin-bottom: 10px;
  letter-spacing: 2px;
  text-transform: uppercase;
`;

const OurText = styled.span`
  color: #0a3b1e;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 100%;
    height: 3px;
    background-color: #c9b8a8;
    border-radius: 2px;
  }
`;

const TeamText = styled.span`
  color: #0a3b1e;
  font-weight: 700;
`;

const TeamDescription = styled.p`
  font-size: 1.2rem;
  color: #0a3b1e;
  max-width: 600px;
  margin: 30px auto 0;
  line-height: 1.6;
  opacity: 0.8;
  font-weight: 300;

  @media (max-width: 768px) {
    font-size: 1.1rem;
    padding: 0 20px;
  }
`;

const DepartmentsSection = styled.div`
  text-align: center;
  margin: 60px 0;
  padding: 0 20px;
`;

const DepartmentButton = styled.button`
  background-color: #c9b8a8;
  color: #0a3b1e;
  border: none;
  padding: 15px 30px;
  margin: 0 10px 15px;
  border-radius: 30px;
  cursor: pointer;
  font-size: 1rem;
  font-weight: 600;
  letter-spacing: 0.5px;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(10, 59, 30, 0.1);

  &:hover {
    background-color: #0a3b1e;
    color: #f8e8d8;
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(10, 59, 30, 0.2);
  }

  &:active {
    transform: translateY(0);
  }

  @media (max-width: 480px) {
    padding: 12px 24px;
    font-size: 0.9rem;
    margin: 0 5px 10px;
  }
`;

const TeamSections = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const TeamSection = styled.div`
  margin-bottom: 80px;

  &:last-child {
    margin-bottom: 40px;
  }
`;

const SectionTitle = styled.h2`
  font-size: 2.5rem;
  font-weight: 700;
  color: #0a3b1e;
  text-align: center;
  margin-bottom: 50px;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: -15px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 4px;
    background-color: #c9b8a8;
    border-radius: 2px;
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -15px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 2px;
    background-color: #c9b8a8;
    border-radius: 1px;
    opacity: 0.6;
  }

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const MembersGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 30px;
  justify-items: center;

  @media (max-width: 480px) {
    grid-template-columns: 1fr;
    gap: 25px;
  }
`;

const MemberCard = styled.div`
  background: #f8e8d8;
  border-radius: 20px;
  padding: 30px 20px;
  text-align: center;
  transition: all 0.3s ease;
  border: 2px solid transparent;
  box-shadow: 0 8px 25px rgba(10, 59, 30, 0.08);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #c9b8a8, #0a3b1e, #c9b8a8);
    border-radius: 20px 20px 0 0;
  }

  &:hover {
    transform: translateY(-8px);
    border-color: #c9b8a8;
    box-shadow: 0 15px 40px rgba(10, 59, 30, 0.15);
  }
`;

const MemberImageStyled = styled.div`
  width: 120px;
  height: 120px;
  border-radius: 50%;
  margin: 0 auto 20px;
  border: 4px solid #c9b8a8;
  overflow: hidden;
  transition: all 0.3s ease;
  position: relative;

  &:hover {
