import React from 'react';
import styled, { keyframes } from 'styled-components';

// Keyframe animations
const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const pulse = keyframes`
  0%, 100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.1);
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -200px 0;
  }
  100% {
    background-position: calc(200px + 100%) 0;
  }
`;

const Footer = () => {
  return (
    <FooterContainer>
      <FooterContent>
        <FooterTop>
          <LogoSection>
            <Logo>LITCLUB</Logo>
            <LogoSubtext>Literary Excellence</LogoSubtext>
          </LogoSection>
          <SocialSection>
            <SocialTitle>Connect With Us</SocialTitle>
            <SocialLinks>
              <SocialIcon href="#" aria-label="Facebook">
                <i className="fab fa-facebook-f"></i>
              </SocialIcon>
              <SocialIcon href="#" aria-label="Instagram">
                <i className="fab fa-instagram"></i>
              </SocialIcon>
              <SocialIcon href="#" aria-label="LinkedIn">
                <i className="fab fa-linkedin-in"></i>
              </SocialIcon>
            </SocialLinks>
          </SocialSection>
        </FooterTop>

        <FooterMiddle>
          <FooterSection>
            <SectionTitle>Quick Links</SectionTitle>
            <FooterLinks>
              <FooterLink href="#">About Us</FooterLink>
              <FooterLink href="#">Our Team</FooterLink>
              <FooterLink href="#">Events</FooterLink>
              <FooterLink href="#">Publications</FooterLink>
            </FooterLinks>
          </FooterSection>

          <FooterSection>
            <SectionTitle>Departments</SectionTitle>
            <FooterLinks>
              <FooterLink href="#">Leadership</FooterLink>
              <FooterLink href="#">Content</FooterLink>
              <FooterLink href="#">Design</FooterLink>
              <FooterLink href="#">Tech</FooterLink>
            </FooterLinks>
          </FooterSection>

          <FooterSection>
            <SectionTitle>Resources</SectionTitle>
            <FooterLinks>
              <FooterLink href="#">Blog</FooterLink>
              <FooterLink href="#">Newsletter</FooterLink>
              <FooterLink href="#">Gallery</FooterLink>
              <FooterLink href="#">Contact</FooterLink>
            </FooterLinks>
          </FooterSection>
        </FooterMiddle>

        <FooterBottom>
          <Copyright>© 2025 LITCLUB. All rights reserved.</Copyright>
          <FooterDecoration />
        </FooterBottom>
      </FooterContent>
    </FooterContainer>
  );
};

// Styled Components
const FooterContainer = styled.footer`
  background: linear-gradient(135deg, #0a3b1e 0%, #0d4a26 100%);
  color: #f8e8d8;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
      radial-gradient(circle at 20% 20%, rgba(248, 232, 216, 0.05) 0%, transparent 50%),
      radial-gradient(circle at 80% 80%, rgba(248, 232, 216, 0.05) 0%, transparent 50%);
    pointer-events: none;
  }
`;

const FooterContent = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  padding: 60px 20px 20px;
  position: relative;
  z-index: 1;
`;

const FooterTop = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-bottom: 40px;
  border-bottom: 2px solid rgba(248, 232, 216, 0.1);
  margin-bottom: 40px;
  animation: ${fadeInUp} 0.8s ease-out;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 30px;
    text-align: center;
  }
`;

const LogoSection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const Logo = styled.div`
  font-size: 2.5rem;
  font-weight: 900;
  letter-spacing: 3px;
  background: linear-gradient(135deg, #f8e8d8 0%, #c9b8a8 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 100%;
    height: 3px;
    background: linear-gradient(90deg, #c9b8a8, transparent);
    border-radius: 2px;
  }
`;

const LogoSubtext = styled.div`
  font-size: 0.9rem;
  color: rgba(248, 232, 216, 0.7);
  letter-spacing: 1px;
  font-weight: 300;
`;

const SocialSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  gap: 15px;

  @media (max-width: 768px) {
    align-items: center;
  }
`;

const SocialTitle = styled.div`
  font-size: 1rem;
  font-weight: 600;
  color: rgba(248, 232, 216, 0.8);
  letter-spacing: 0.5px;
`;

const SocialLinks = styled.div`
  display: flex;
  gap: 15px;
`;

const SocialIcon = styled.a`
  width: 45px;
  height: 45px;
  border-radius: 50%;
  background: linear-gradient(135deg, rgba(248, 232, 216, 0.1) 0%, rgba(248, 232, 216, 0.2) 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
  border: 1px solid rgba(248, 232, 216, 0.2);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(248, 232, 216, 0.3), transparent);
    transition: left 0.5s;
  }

  i {
    font-size: 1.2rem;
    color: #f8e8d8;
    transition: all 0.3s ease;
    position: relative;
    z-index: 1;
  }

  &:hover {
    transform: translateY(-3px) scale(1.1);
    background: linear-gradient(135deg, rgba(248, 232, 216, 0.2) 0%, rgba(248, 232, 216, 0.3) 100%);
    box-shadow: 0 8px 20px rgba(0, 0, 0, 0.3);

    &::before {
      left: 100%;
    }

    i {
      animation: ${pulse} 0.6s ease-in-out;
    }
  }
`;

const FooterMiddle = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
